import java.util.*;
class LargestProductInASeries{
	static final int VAL = 13;

	public static void main(String [] args){
		//Scanner in = new Scanner(System.in);
		System.out.println("Input string: ");
		String nums = "";
		System.out.println("DONE");
		int a[] = new int[1000];
		for(int i = 0; i < 1000; i++){
			a[i]=Integer.parseInt(""+nums.charAt(i), 10);
		}
		long max = 1;
		int mI = 0;
		for(int i = 0; i < 1000-VAL; i++){
			long tmp = findProd(a, i);
			if(tmp>max){
				max=tmp;
				mI=i;
				prnt(a, i);
				System.out.println(max);
			}
		}
		System.out.println("product of "+VAL+" = "+max+" at index of "+mI);
	}

	public static long findProd(int[] nums, int i){
		int count = 0;
		long product = 1;
		for(count=0;count<VAL;count++){
			product = product*(long)nums[count+i];
		}
		return product;
	}
	public static void prnt(int[] a, int i){
		System.out.print("Product of ");
		for(int x = 0; x<VAL; x++){
			System.out.print(a[x+i]+" * ");
		}
		System.out.print(" = ");
	}
}